css:
/* Estilos da página Artigos/Blog - baseado em QuemSomos */

.artigos-page {
  min-height: 100vh;
  background: linear-gradient(to bottom right, #FAF9F7, #F5F3F0);
}

/* Hero Section */
.artigos-hero {
  padding: 4rem 1rem;
  text-align: center;
  background: linear-gradient(135deg, rgba(207, 175, 90, 0.1), rgba(74, 144, 169, 0.1));
}

.artigos-hero h1 {
  font-size: 3rem;
  font-weight: 700;
  color: #2D3748;
  margin-bottom: 1rem;
}

.artigos-hero .subtitle {
  font-size: 1.5rem;
  color: #CFAF5A;
  margin-bottom: 1rem;
}

.artigos-hero .description {
  font-size: 1.125rem;
  color: #4A5568;
  max-width: 700px;
  margin: 0 auto;
}

/* Categories Section */
.categories-section {
  padding: 3rem 1rem;
}

.category-card {
  transition: all 0.3s ease;
  border: 2px solid transparent;
}

.category-card:hover {
  border-color: #CFAF5A;
  transform: translateY(-4px);
  box-shadow: 0 10px 30px rgba(207, 175, 90, 0.2);
}

/* Featured Articles */
.featured-section {
  padding: 3rem 1rem;
  background: rgba(255, 255, 255, 0.5);
}

.article-card {
  height: 100%;
  transition: all 0.3s ease;
  cursor: pointer;
}

.article-card:hover {
  transform: translateY(-8px);
  box-shadow: 0 15px 40px rgba(0, 0, 0, 0.1);
}

.article-image {
  width: 100%;
  height: 200px;
  object-fit: cover;
  border-radius: 0.5rem 0.5rem 0 0;
}

.article-category {
  display: inline-block;
  padding: 0.25rem 0.75rem;
  background: rgba(207, 175, 90, 0.2);
  color: #CFAF5A;
  border-radius: 1rem;
  font-size: 0.875rem;
  font-weight: 600;
  margin-bottom: 0.5rem;
}

.article-title {
  font-size: 1.5rem;
  font-weight: 700;
  color: #2D3748;
  margin-bottom: 0.5rem;
}

.article-excerpt {
  color: #718096;
  margin-bottom: 1rem;
  line-height: 1.6;
}

.article-meta {
  display: flex;
  align-items: center;
  gap: 1rem;
  color: #A0AEC0;
  font-size: 0.875rem;
}

/* Recent Articles */
.recent-section {
  padding: 3rem 1rem;
}

.article-list-item {
  padding: 1.5rem;
  border-bottom: 1px solid #E2E8F0;
  transition: all 0.3s ease;
}

.article-list-item:hover {
  background: rgba(207, 175, 90, 0.05);
  padding-left: 2rem;
}

.article-list-item:last-child {
  border-bottom: none;
}

/* CTA Section */
.cta-section {
  padding: 4rem 1rem;
  text-align: center;
  background: linear-gradient(135deg, rgba(207, 175, 90, 0.15), rgba(74, 144, 169, 0.15));
}

.cta-section h2 {
  font-size: 2rem;
  color: #2D3748;
  margin-bottom: 1rem;
}

.cta-section p {
  font-size: 1.125rem;
  color: #4A5568;
  margin-bottom: 2rem;
  max-width: 600px;
  margin-left: auto;
  margin-right: auto;
}

/* Responsive */
@media (max-width: 768px) {
  .artigos-hero h1 {
    font-size: 2rem;
  }
  
  .artigos-hero .subtitle {
    font-size: 1.25rem;
  }
  
  .article-title {
    font-size: 1.25rem;
  }
}

